create table t_delivery (id bigint generated by default as identity, arrive_store_time timestamp(6), cancel_code varchar(255), cancel_message varchar(255), cancel_time timestamp(6), channel varchar(255), channel_order_id varchar(255), channel_price integer, channel_receive_time timestamp(6), complete_time timestamp(6), create_time timestamp(6), delivering_time timestamp(6), error_code varchar(255), error_message varchar(255), error_time timestamp(6), except_time timestamp(6), order_code varchar(255), order_id bigint, push_time timestamp(6), rider_name varchar(255), rider_phone varchar(255), rider_receive_time timestamp(6), status integer, update_time timestamp(6), primary key (id));
create table t_order (id bigint generated by default as identity, callback_url varchar(255), create_time timestamp(6), create_user varchar(255), is_mock boolean, order_code varchar(255), order_time timestamp(6), receive_user_address varchar(255), receive_user_name varchar(255), receive_user_phone varchar(255), store_code varchar(255), total_price integer, weight_info varchar(255), primary key (id));
create table t_plan (id bigint generated by default as identity, create_time timestamp(6), create_user varchar(255), "desc" varchar(255), plan_name varchar(255), time_period integer, type integer, update_time timestamp(6), update_user varchar(255), primary key (id));
create table t_plan_channel_mapping (id bigint generated by default as identity, channel varchar(255), create_time timestamp(6), create_user varchar(255), plan_id bigint, sort integer, primary key (id));
create table t_product_info (id bigint generated by default as identity, order_code varchar(255), product_name varchar(255), product_num integer, product_remark varchar(255), primary key (id));
create table t_region (id integer not null, name varchar(255), parent_id integer, primary key (id));
create table t_store (id bigint generated by default as identity, address varchar(255), city_id integer, create_time timestamp(6), create_user varchar(255), district_id integer, latitude varchar(255), longitude varchar(255), plan_id bigint, province_id integer, store_code varchar(255), store_contract varchar(255), store_contract_phone varchar(255), store_name varchar(255), update_time timestamp(6), update_user varchar(255), primary key (id));
